import{$ as e,U as r,S as a,av as t}from"./chunks/framework.BHI8-lmo.js";const s=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"categories/history/六朝/何为六朝.md","filePath":"categories/history/六朝/何为六朝.md","lastUpdated":null}'),i={name:"categories/history/六朝/何为六朝.md"},l=t('<p>​ 六朝（222年–589年），一般是指<a href="https://baike.baidu.com/item/%E4%B8%AD%E5%9B%BD%E5%8E%86%E5%8F%B2/152769?fromModule=lemma_inlink" target="_blank" rel="noreferrer">中国历史</a>上<a href="https://baike.baidu.com/item/%E4%B8%89%E5%9B%BD/5428?fromModule=lemma_inlink" target="_blank" rel="noreferrer">三国</a>至南北朝的<strong>南方的六个朝代</strong>。即<a href="https://baike.baidu.com/item/%E5%AD%99%E5%90%B4/3129077?fromModule=lemma_inlink" target="_blank" rel="noreferrer">孙吴</a>（或称东吴、三国吴）、<a href="https://baike.baidu.com/item/%E4%B8%9C%E6%99%8B/456028?fromModule=lemma_inlink" target="_blank" rel="noreferrer">东晋</a>、<a href="https://baike.baidu.com/item/%E5%8D%97%E6%9C%9D%E5%AE%8B/913897?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南朝宋</a>（或称刘宋）、<a href="https://baike.baidu.com/item/%E5%8D%97%E6%9C%9D%E9%BD%90/9615748?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南朝齐</a>（或称萧齐）、<a href="https://baike.baidu.com/item/%E5%8D%97%E6%9C%9D%E6%A2%81/9617814?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南朝梁</a>（或称萧梁）、<a href="https://baike.baidu.com/item/%E5%8D%97%E6%9C%9D%E9%99%88/913989?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南朝陈</a>这六个朝代。又因六朝皆以<a href="https://baike.baidu.com/item/%E5%BB%BA%E5%BA%B7/8714?fromModule=lemma_inlink" target="_blank" rel="noreferrer">建康</a>（今<a href="https://baike.baidu.com/item/%E5%8D%97%E4%BA%AC/23952?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南京</a>）为京师，所以后世许多文献皆以六朝或<a href="https://baike.baidu.com/item/%E5%8D%97%E6%9C%9D/7131305?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南朝</a>来代指<a href="https://baike.baidu.com/item/%E5%8D%97%E4%BA%AC/23952?fromModule=lemma_inlink" target="_blank" rel="noreferrer">南京</a>，并常会用南京来指明六朝时期的人事物，<strong>所以南京又称<a href="https://baike.baidu.com/item/%E5%85%AD%E6%9C%9D%E5%8F%A4%E9%83%BD/26234?fromModule=lemma_inlink" target="_blank" rel="noreferrer">六朝古都</a></strong>。</p><p>​ 六朝承汉启唐，创造了极其辉煌灿烂的“六朝文明”，在科技、文学、艺术等诸方面均达到了空前的繁荣，中国南方得到巨大发展，开创了<a href="https://baike.baidu.com/item/%E4%B8%AD%E5%8D%8E%E6%96%87%E6%98%8E/4207?fromModule=lemma_inlink" target="_blank" rel="noreferrer">中华文明</a>新的历史纪元。这六个朝代的共同点是都建都于南京，因此南京被称为“六朝古都”，六朝时期的南京城是世界上第一个人口超过百万的城市，唐朝人<a href="https://baike.baidu.com/item/%E8%AE%B8%E5%B5%A9/5469296?fromModule=lemma_inlink" target="_blank" rel="noreferrer">许嵩</a>在《<a href="https://baike.baidu.com/item/%E5%BB%BA%E5%BA%B7%E5%AE%9E%E5%BD%95/3259629?fromModule=lemma_inlink" target="_blank" rel="noreferrer">建康实录</a>》一书记载了这六个朝代，故而得名。</p><ul><li><p>孙吴/吴国 (229年5月23-280年5月1日)， 开国皇帝：孙权</p><p>注释：<a href="https://baike.baidu.com/item/%E5%AD%99%E6%9D%83/17337?fromModule=lemma_inlink" target="_blank" rel="noreferrer">孙权</a>于222年被魏文帝曹丕封为吴王，229年在<a href="https://baike.baidu.com/item/%E6%AD%A6%E6%98%8C/3508122?fromModule=lemma_inlink" target="_blank" rel="noreferrer">武昌</a>（今湖北<a href="https://baike.baidu.com/item/%E9%84%82%E5%9F%8E/462409?fromModule=lemma_inlink" target="_blank" rel="noreferrer">鄂城</a>）称帝，国号“吴”，改元黄龙元年，史称“东吴”。后又迁都建业。</p></li><li><p>东晋 (317-420)， 开国皇帝： 司马睿</p></li><li><p>南朝宋 (420-479)， 开国皇帝： 刘裕</p></li><li><p>南朝齐(479-502)， 开国皇帝： 萧道成</p></li><li><p>南朝梁 (502-557)， 开国皇帝： 萧衍</p></li><li><p>南朝陈 (557-589)， 开国皇帝： 陈霸先</p></li></ul>',3),o=[l];function m(n,_,f,E,k,b){return a(),r("div",null,o)}const p=e(i,[["render",m]]);export{s as __pageData,p as default};
