import{$ as t,U as e,S as l,av as d}from"./chunks/framework.BHI8-lmo.js";const m=JSON.parse('{"title":"从 Windows 过度到 Mac 必备快捷键对照表","description":"","frontmatter":{},"headers":[],"relativePath":"essay/2024/从 Windows 过度到 Mac 必备快捷键对照表.md","filePath":"essay/2024/从 Windows 过度到 Mac 必备快捷键对照表.md","lastUpdated":1721020987000}'),a={name:"essay/2024/从 Windows 过度到 Mac 必备快捷键对照表.md"},i=d('<h1 id="从-windows-过度到-mac-必备快捷键对照表" tabindex="-1">从 Windows 过度到 Mac 必备快捷键对照表<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#frontmatter-title" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#从-windows-过度到-mac-必备快捷键对照表" aria-label="Permalink to &quot;从 Windows 过度到 Mac 必备快捷键对照表[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#frontmatter-title)&quot;">​</a></h1><blockquote><p>摘自：<a href="https://github.com/judasn/IntelliJ-IDEA-Tutorial/blob/master/keymap-win-mac.md" target="_blank" rel="noreferrer">IntelliJ-IDEA-Tutorial/keymap-win-mac.md</a></p></blockquote><h2 id="mac-键盘符号" tabindex="-1">Mac 键盘符号<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#mac-%E9%94%AE%E7%9B%98%E7%AC%A6%E5%8F%B7" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#mac-键盘符号" aria-label="Permalink to &quot;Mac 键盘符号[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#mac-键盘符号)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">图标</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">⌘</td><td style="text-align:left;">Command</td></tr><tr><td style="text-align:left;">⇧</td><td style="text-align:left;">Shift</td></tr><tr><td style="text-align:left;">⇪</td><td style="text-align:left;">Caps Lock</td></tr><tr><td style="text-align:left;">⌥</td><td style="text-align:left;">Option = Alt</td></tr><tr><td style="text-align:left;">⌃</td><td style="text-align:left;">Control</td></tr><tr><td style="text-align:left;">↩</td><td style="text-align:left;">Enter</td></tr><tr><td style="text-align:left;">⌫</td><td style="text-align:left;">Delete</td></tr><tr><td style="text-align:left;">⌦</td><td style="text-align:left;">Fn + Delete</td></tr><tr><td style="text-align:left;">↑</td><td style="text-align:left;">上箭头</td></tr><tr><td style="text-align:left;">↓</td><td style="text-align:left;">下箭头</td></tr><tr><td style="text-align:left;">←</td><td style="text-align:left;">左箭头</td></tr><tr><td style="text-align:left;">→</td><td style="text-align:left;">右箭头</td></tr><tr><td style="text-align:left;">⇞</td><td style="text-align:left;">Fn + ↑ = Page Up</td></tr><tr><td style="text-align:left;">⇟</td><td style="text-align:left;">Fn + ↓ = Page Down</td></tr><tr><td style="text-align:left;">Home</td><td style="text-align:left;">Fn + ←</td></tr><tr><td style="text-align:left;">End</td><td style="text-align:left;">Fn + →</td></tr><tr><td style="text-align:left;">⇥</td><td style="text-align:left;">Tab = 右制表符</td></tr><tr><td style="text-align:left;">⇤</td><td style="text-align:left;">Shift + Tab = 左制表符</td></tr><tr><td style="text-align:left;">⎋</td><td style="text-align:left;">Esc = Escape</td></tr><tr><td style="text-align:left;">⏏</td><td style="text-align:left;">电源开关键</td></tr></tbody></table><h2 id="alt" tabindex="-1">Alt<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#alt" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#alt" aria-label="Permalink to &quot;Alt[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#alt)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Alt + `</td><td style="text-align:left;">Control + V</td><td style="text-align:left;">显示版本控制常用操作菜单弹出层</td></tr><tr><td style="text-align:left;">Alt + F1</td><td style="text-align:left;">Option + F1</td><td style="text-align:left;">显示当前文件选择目标弹出层，弹出层中有很多目标可以进行选择</td></tr><tr><td style="text-align:left;">Alt + F7</td><td style="text-align:left;">Option + F7</td><td style="text-align:left;">查询所选对象/变量被引用</td></tr><tr><td style="text-align:left;">Alt + Enter</td><td style="text-align:left;">Option + Enter</td><td style="text-align:left;">IntelliJ IDEA 根据光标所在问题，提供快速修复选择，光标放在的位置不同提示的结果也不同</td></tr><tr><td style="text-align:left;">Alt + Insert</td><td style="text-align:left;">Command + N</td><td style="text-align:left;">代码自动生成，如生成对象的 set / get 方法，构造函数，toString() 等</td></tr><tr><td style="text-align:left;">Alt + 左方向键</td><td style="text-align:left;">Control + 左方向键</td><td style="text-align:left;">切换当前已打开的窗口中的子视图，比如 Debug 窗口中有 Output、Debugger 等子视图，用此快捷键就可以在子视图中切换</td></tr><tr><td style="text-align:left;">Alt + 右方向键</td><td style="text-align:left;">Control + 右方向键</td><td style="text-align:left;">切换当前已打开的窗口中的子视图，比如 Debug 窗口中有 Output、Debugger 等子视图，用此快捷键就可以在子视图中切换</td></tr><tr><td style="text-align:left;">Alt + 前方向键</td><td style="text-align:left;">Control + 前方向键</td><td style="text-align:left;">当前光标跳转到当前文件的前一个方法名位置</td></tr><tr><td style="text-align:left;">Alt + 后方向键</td><td style="text-align:left;">Control + 后方向键</td><td style="text-align:left;">当前光标跳转到当前文件的后一个方法名位置</td></tr><tr><td style="text-align:left;">Alt + 1,2,3...9</td><td style="text-align:left;">Command + 1,2,3...9</td><td style="text-align:left;">显示对应数值的选项卡，其中 1 是 Project 用得最多</td></tr></tbody></table><h2 id="ctrl" tabindex="-1">Ctrl<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#ctrl" aria-label="Permalink to &quot;Ctrl[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Ctrl + F</td><td style="text-align:left;">Command + F</td><td style="text-align:left;">在当前文件进行文本查找</td></tr><tr><td style="text-align:left;">Ctrl + R</td><td style="text-align:left;">Command + R</td><td style="text-align:left;">在当前文件进行文本替换</td></tr><tr><td style="text-align:left;">Ctrl + Z</td><td style="text-align:left;">Command + Z</td><td style="text-align:left;">撤销</td></tr><tr><td style="text-align:left;">Ctrl + G</td><td style="text-align:left;">Command + L</td><td style="text-align:left;">跳转到指定行数位置</td></tr><tr><td style="text-align:left;">Ctrl + Y</td><td style="text-align:left;">Command + Delete</td><td style="text-align:left;">删除光标所在行 或 删除选中的行</td></tr><tr><td style="text-align:left;">Ctrl + D</td><td style="text-align:left;">Command + D</td><td style="text-align:left;">复制光标所在行 或 复制选择内容，并把复制内容插入光标位置下面</td></tr><tr><td style="text-align:left;">Ctrl + W</td><td style="text-align:left;">Option + 方向键上</td><td style="text-align:left;">递进式选择代码块。可选中光标所在的单词或段落，连续按会在原有选中的基础上再扩展选中范围</td></tr><tr><td style="text-align:left;">Ctrl + E</td><td style="text-align:left;">Command + E</td><td style="text-align:left;">显示最近打开的文件记录列表</td></tr><tr><td style="text-align:left;">Ctrl + N</td><td style="text-align:left;">Command + O</td><td style="text-align:left;">根据输入的 <strong>类名</strong> 查找类文件</td></tr><tr><td style="text-align:left;">Ctrl + J</td><td style="text-align:left;">Command + J</td><td style="text-align:left;">插入自定义动态代码模板</td></tr><tr><td style="text-align:left;">Ctrl + P</td><td style="text-align:left;">Command + P</td><td style="text-align:left;">方法参数提示显示</td></tr><tr><td style="text-align:left;">Ctrl + Q</td><td style="text-align:left;">Control + J</td><td style="text-align:left;">当前位置变量、方法的 Documentation 内容显示</td></tr><tr><td style="text-align:left;">Ctrl + U</td><td style="text-align:left;">Command + U</td><td style="text-align:left;">前往当前光标所在的方法的父类的方法 / 接口定义</td></tr><tr><td style="text-align:left;">Ctrl + B</td><td style="text-align:left;">Command + B</td><td style="text-align:left;">进入光标所在的方法/变量的接口或是定义处，等效于 <code>Ctrl + 左键单击</code></td></tr><tr><td style="text-align:left;">Ctrl + /</td><td style="text-align:left;">Command + /</td><td style="text-align:left;">注释光标所在行代码，会根据当前不同文件类型使用不同的注释符号</td></tr><tr><td style="text-align:left;">Ctrl + F1</td><td style="text-align:left;">Command + F1</td><td style="text-align:left;">在光标所在的错误代码处显示错误信息</td></tr><tr><td style="text-align:left;">Ctrl + F11</td><td style="text-align:left;">Option + F3</td><td style="text-align:left;">选中文件 / 文件夹，使用助记符设定 / 取消书签</td></tr><tr><td style="text-align:left;">Ctrl + F12</td><td style="text-align:left;">Command + F12</td><td style="text-align:left;">弹出当前文件结构层，可以在弹出的层上直接输入，进行筛选</td></tr><tr><td style="text-align:left;">Ctrl + Space</td><td style="text-align:left;">Control + Space</td><td style="text-align:left;">基础代码补全，默认在 Windows 系统上被输入法占用，需要进行修改，建议修改为 <code>Ctrl + 逗号</code></td></tr><tr><td style="text-align:left;">Ctrl + Delete</td><td style="text-align:left;">Option + Fn+ Delete</td><td style="text-align:left;">删除光标后面的单词或是中文句</td></tr><tr><td style="text-align:left;">Ctrl + BackSpace</td><td style="text-align:left;">Option + Delete</td><td style="text-align:left;">删除光标前面的单词或是中文句</td></tr><tr><td style="text-align:left;">Ctrl + 1,2,3...9</td><td style="text-align:left;">Control + 1,2,3...9</td><td style="text-align:left;">定位到对应数值的书签位置</td></tr><tr><td style="text-align:left;">Ctrl + 加号</td><td style="text-align:left;">Command + 加号</td><td style="text-align:left;">展开代码</td></tr><tr><td style="text-align:left;">Ctrl + 减号</td><td style="text-align:left;">Command + 减号</td><td style="text-align:left;">折叠代码</td></tr><tr><td style="text-align:left;">Ctrl + 左键单击</td><td style="text-align:left;">Control + 左键单击</td><td style="text-align:left;">在打开的文件标题上，弹出该文件路径</td></tr><tr><td style="text-align:left;">Ctrl + 左方向键</td><td style="text-align:left;">Option + 左方向键</td><td style="text-align:left;">光标跳转到当前单词 / 中文句的左侧开头位置</td></tr><tr><td style="text-align:left;">Ctrl + 右方向键</td><td style="text-align:left;">Option + 右方向键</td><td style="text-align:left;">光标跳转到当前单词 / 中文句的右侧开头位置</td></tr><tr><td style="text-align:left;">Ctrl + 前方向键</td><td style="text-align:left;">预设中没有该快捷键</td><td style="text-align:left;">等效于鼠标滚轮向前效果</td></tr><tr><td style="text-align:left;">Ctrl + 后方向键</td><td style="text-align:left;">预设中没有该快捷键</td><td style="text-align:left;">等效于鼠标滚轮向后效果</td></tr></tbody></table><h2 id="shift" tabindex="-1">Shift<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#shift" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#shift" aria-label="Permalink to &quot;Shift[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#shift)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Shift + F11</td><td style="text-align:left;">Command + F3</td><td style="text-align:left;">弹出书签显示层</td></tr><tr><td style="text-align:left;">Shift + Tab</td><td style="text-align:left;">Shift + Tab</td><td style="text-align:left;">取消缩进</td></tr><tr><td style="text-align:left;">Shift + Enter</td><td style="text-align:left;">Shift + Enter</td><td style="text-align:left;">开始新一行。光标所在行下空出一行，光标定位到新行位置</td></tr><tr><td style="text-align:left;">Shift + 左键单击</td><td style="text-align:left;">Shift + 左键单击</td><td style="text-align:left;">在打开的文件名上按此快捷键，可以关闭当前打开文件</td></tr></tbody></table><h2 id="alt-shift" tabindex="-1">Alt + Shift<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#alt-shift" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#alt-shift" aria-label="Permalink to &quot;Alt + Shift[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#alt-shift)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Alt + Shift + N</td><td style="text-align:left;">Option + Shift + N</td><td style="text-align:left;">选择 / 添加 task</td></tr><tr><td style="text-align:left;">Alt + Shift + 左键双击</td><td style="text-align:left;">Option + Shift + 左键双击</td><td style="text-align:left;">选择被双击的单词 / 中文句，按住不放，可以同时选择其他单词 / 中文句</td></tr><tr><td style="text-align:left;">Alt + Shift + 前方向键</td><td style="text-align:left;">Option + Shift + 前方向键</td><td style="text-align:left;">移动光标所在行向上移动</td></tr><tr><td style="text-align:left;">Alt + Shift + 后方向键</td><td style="text-align:left;">Option + Shift + 后方向键</td><td style="text-align:left;">移动光标所在行向下移动</td></tr></tbody></table><h2 id="ctrl-alt" tabindex="-1">Ctrl + Alt<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl-alt" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#ctrl-alt" aria-label="Permalink to &quot;Ctrl + Alt[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl-alt)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Ctrl + Alt + L</td><td style="text-align:left;">Command + Option + L</td><td style="text-align:left;">格式化代码，可以对当前文件和整个包目录使用（有些文件格式化是有特殊要求的，比如 SQL 文件必须设置方言才可被格式化）</td></tr><tr><td style="text-align:left;">Ctrl + Alt + O</td><td style="text-align:left;">Control + Option + O</td><td style="text-align:left;">优化导入的类，可以对当前文件和整个包目录使用</td></tr><tr><td style="text-align:left;">Ctrl + Alt + T</td><td style="text-align:left;">Command + Option + T</td><td style="text-align:left;">对选中的代码弹出环绕选项弹出层</td></tr><tr><td style="text-align:left;">Ctrl + Alt + S</td><td style="text-align:left;">Command + 逗号</td><td style="text-align:left;">打开 IntelliJ IDEA 系统设置</td></tr><tr><td style="text-align:left;">Ctrl + Alt + Enter</td><td style="text-align:left;">Command + Option + Enter</td><td style="text-align:left;">光标所在行上空出一行，光标定位到新行</td></tr><tr><td style="text-align:left;">Ctrl + Alt + 左方向键</td><td style="text-align:left;">Command + Option + 左方向键</td><td style="text-align:left;">退回到上一个操作的地方</td></tr><tr><td style="text-align:left;">Ctrl + Alt + 右方向键</td><td style="text-align:left;">Command + Option + 右方向键</td><td style="text-align:left;">前进到上一个操作的地方</td></tr></tbody></table><h2 id="ctrl-shift" tabindex="-1">Ctrl + Shift<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl-shift" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#ctrl-shift" aria-label="Permalink to &quot;Ctrl + Shift[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl-shift)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Ctrl + Shift + F</td><td style="text-align:left;">Command + Shift + F</td><td style="text-align:left;">根据输入内容查找整个项目 或 指定目录内文件</td></tr><tr><td style="text-align:left;">Ctrl + Shift + R</td><td style="text-align:left;">Command + Shift + R</td><td style="text-align:left;">根据输入内容替换对应内容，范围为整个项目 或 指定目录内文件</td></tr><tr><td style="text-align:left;">Ctrl + Shift + J</td><td style="text-align:left;">Control + Shift + J</td><td style="text-align:left;">自动将下一行合并到当前行末尾</td></tr><tr><td style="text-align:left;">Ctrl + Shift + Z</td><td style="text-align:left;">Command + Shift + Z</td><td style="text-align:left;">取消撤销</td></tr><tr><td style="text-align:left;">Ctrl + Shift + W</td><td style="text-align:left;">Option + 方向键下</td><td style="text-align:left;">递进式取消选择代码块。可选中光标所在的单词或段落，连续按会在原有选中的基础上再扩展取消选中范围</td></tr><tr><td style="text-align:left;">Ctrl + Shift + N</td><td style="text-align:left;">Command + Shift + O</td><td style="text-align:left;">通过文件名定位 / 打开文件 / 目录，打开目录需要在输入的内容后面多加一个正斜杠</td></tr><tr><td style="text-align:left;">Ctrl + Shift + U</td><td style="text-align:left;">Command + Shift + U</td><td style="text-align:left;">对选中的代码进行大 / 小写轮流转换</td></tr><tr><td style="text-align:left;">Ctrl + Shift + T</td><td style="text-align:left;">Command + Shift + T</td><td style="text-align:left;">对当前类生成单元测试类，如果已经存在的单元测试类则可以进行选择</td></tr><tr><td style="text-align:left;">Ctrl + Shift + C</td><td style="text-align:left;">Command + Shift + C</td><td style="text-align:left;">复制当前文件磁盘路径到剪贴板</td></tr><tr><td style="text-align:left;">Ctrl + Shift + B</td><td style="text-align:left;">Control + Shift + B</td><td style="text-align:left;">跳转到类型声明处</td></tr><tr><td style="text-align:left;">Ctrl + Shift + /</td><td style="text-align:left;">Command + Option + /</td><td style="text-align:left;">代码块注释</td></tr><tr><td style="text-align:left;">Ctrl + Shift + [</td><td style="text-align:left;">Command + Shift + [</td><td style="text-align:left;">选中从光标所在位置到它的顶部中括号位置</td></tr><tr><td style="text-align:left;">Ctrl + Shift + ]</td><td style="text-align:left;">Command + Shift + ]</td><td style="text-align:left;">选中从光标所在位置到它的底部中括号位置</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 加号</td><td style="text-align:left;">Command + Shift + 加号</td><td style="text-align:left;">展开所有代码</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 减号</td><td style="text-align:left;">Command + Shift + 减号</td><td style="text-align:left;">折叠所有代码</td></tr><tr><td style="text-align:left;">Ctrl + Shift + F7</td><td style="text-align:left;">Command + Shift + F7</td><td style="text-align:left;">高亮显示所有该选中文本，按 Esc 高亮消失</td></tr><tr><td style="text-align:left;">Ctrl + Shift + F12</td><td style="text-align:left;">Command + Shift + F12</td><td style="text-align:left;">编辑器最大化</td></tr><tr><td style="text-align:left;">Ctrl + Shift + Enter</td><td style="text-align:left;">Command + Shift + Enter</td><td style="text-align:left;">自动结束代码，行末自动添加分号</td></tr><tr><td style="text-align:left;">Ctrl + Shift + Backspace</td><td style="text-align:left;">Ctrl + Shift + Backspace</td><td style="text-align:left;">退回到上次修改的地方</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 1,2,3...9</td><td style="text-align:left;">Control + Shift + 1,2,3...9</td><td style="text-align:left;">快速添加指定数值的书签</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 左键单击</td><td style="text-align:left;">Command + Shift + 左键单击</td><td style="text-align:left;">把光标放在某个类变量上，按此快捷键可以直接定位到该类中</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 左方向键</td><td style="text-align:left;">Option + Shift + 左方向键</td><td style="text-align:left;">在代码文件上，光标跳转到当前单词 / 中文句的左侧开头位置，同时选中该单词 / 中文句</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 右方向键</td><td style="text-align:left;">Option + Shift + 右方向键</td><td style="text-align:left;">在代码文件上，光标跳转到当前单词 / 中文句的右侧开头位置，同时选中该单词 / 中文句</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 前方向键</td><td style="text-align:left;">Command + Shift + 前方向键</td><td style="text-align:left;">光标放在方法名上，将方法移动到上一个方法前面，调整方法排序</td></tr><tr><td style="text-align:left;">Ctrl + Shift + 后方向键</td><td style="text-align:left;">Command + Shift + 后方向键</td><td style="text-align:left;">光标放在方法名上，将方法移动到下一个方法前面，调整方法排序</td></tr></tbody></table><h2 id="ctrl-shift-alt" tabindex="-1">Ctrl + Shift + Alt<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl-shift-alt" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#ctrl-shift-alt" aria-label="Permalink to &quot;Ctrl + Shift + Alt[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#ctrl-shift-alt)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">Ctrl + Shift + Alt + V</td><td style="text-align:left;">Command + Shift + Option + V</td><td style="text-align:left;">无格式黏贴</td></tr><tr><td style="text-align:left;">Ctrl + Shift + Alt + S</td><td style="text-align:left;">Command + ;</td><td style="text-align:left;">打开当前项目设置</td></tr></tbody></table><h2 id="other" tabindex="-1">Other<a href="https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#other" target="_blank" rel="noreferrer"></a> <a class="header-anchor" href="#other" aria-label="Permalink to &quot;Other[](https://buqiyuan.github.io/essay/2021/keymap-win-mac.html#other)&quot;">​</a></h2><table tabindex="0"><thead><tr><th style="text-align:left;">Win 快捷键</th><th style="text-align:left;">Mac 快捷键</th><th style="text-align:left;">介绍</th></tr></thead><tbody><tr><td style="text-align:left;">F2</td><td style="text-align:left;">F2</td><td style="text-align:left;">跳转到下一个高亮错误 或 警告位置</td></tr><tr><td style="text-align:left;">F4</td><td style="text-align:left;">F4</td><td style="text-align:left;">编辑源</td></tr><tr><td style="text-align:left;">F11</td><td style="text-align:left;">F3</td><td style="text-align:left;">添加书签</td></tr><tr><td style="text-align:left;">F12</td><td style="text-align:left;">F12</td><td style="text-align:left;">回到前一个工具窗口</td></tr><tr><td style="text-align:left;">Tab</td><td style="text-align:left;">Tab</td><td style="text-align:left;">缩进</td></tr><tr><td style="text-align:left;">ESC</td><td style="text-align:left;">ESC</td><td style="text-align:left;">从工具窗口进入代码文件窗口</td></tr></tbody></table>',20),n=[i];function r(s,f,y,g,x,h){return l(),e("div",null,n)}const C=t(a,[["render",r]]);export{m as __pageData,C as default};
